import React, { Component } from "react";
import {
  Menu,
  Row,
  Col,
  Input,
  Icon,
  Popover,
  Badge,
  notification
} from "antd";
import "antd/dist/antd.css";
import "./App.css";

class App extends Component {
  state = {
    notifications: []
  };

  componentDidMount = () => {
    let notifications = this.state.notifications;

    notifications.push({ msg: "UserY commented on your post", isRead: false });
    notifications.push({ msg: "UserX started following you", isRead: true });
    this.setState({ notifications });
  };

  render() {
    const text = <span>Notifications</span>;
    const content = (
      <div>
        {this.state.notifications.forEach(notification => (
          <p>notification.msg</p>
        ))}
      </div>
    );

    return (
      <div className="App">
        <div className="menuBar">
          <div className="logo">
            <a href="">Push Notification</a>
            count: {this.state.notifications.length}
          </div>
          <div className="menuCon">
            <div className="rightMenu">
              <a href="">
                <Popover
                  placement="bottomRight"
                  title={text}
                  content={content}
                  trigger="click"
                >
                  <Badge count={this.state.notifications.length} dot>
                    <Icon type="bell" />
                  </Badge>
                </Popover>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
