import React from "react";
import logo from "./logo.svg";
import { Menu, Row, Col, Input, Icon, Popover, Badge } from "antd";
import "antd/dist/antd.css";
import "./App.css";

function App() {
  state = {
    count: 5,
    show: true
  };
  const text = <span>Notifications</span>;
  const content = (
    <div>
      <p>Content</p>
      <p>Content</p>
    </div>
  );

  return (
    <div className="App">
      <div className="menuBar">
        <div className="logo">
          <a href="">Push Notification</a>
        </div>
        <div className="menuCon">
          <div className="rightMenu">
            <a href="">
              <Popover
                placement="bottomRight"
                title={text}
                content={content}
                trigger="click"
              >
                <Badge count={this.state.count}>
                  <Icon type="bell" />
                </Badge>
              </Popover>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
