import React, { Component } from "react";
import { Icon, Popover, Badge, List, notification } from "antd";
import "antd/dist/antd.css";
import "./App.css";

class App extends Component {
  state = {
    todos: [],
    todoId: 0
  };

  componentDidMount = () => {};

  addTodo = text => {
    const todos = this.state.todos;
    const todo = { id: this.state.todoId + 1, text: text, completed: false };
    const newTodos = [...todos, todo];
    this.setState({ todos: newTodos, todoId: this.state.todoId + 1 });
  };

  render() {
    return (
      <div className="App">
        <List
          dataSource={this.state.todos}
          renderItem={todo => (
            <List.Item
              className={notification.isRead ? "item-read" : "item-not-read"}
              onClick={() => this.handleItemClick(notification)}
            >
              <span style={{ padding: "2px 20px" }}>
                <b>{notification.name}</b> {notification.msg}
              </span>
            </List.Item>
          )}
        />
      </div>
    );
  }
}

export default App;
