import React, { Component } from "react";
import { Icon, Popover, Badge, List, notification, Typography } from "antd";
import "antd/dist/antd.css";
import "./App.css";

class App extends Component {
  state = {
    todos: [],
    todoId: 0
  };

  componentDidMount = () => {
    this.addTodo("test1");
  };

  addTodo = text => {
    const todos = this.state.todos;
    const todo = { id: this.state.todoId + 1, text: text, completed: false };
    const newTodos = [...todos, todo];
    this.setState({ todos: newTodos, todoId: this.state.todoId + 1 });
  };

  render() {
    return (
      <div className="App">
        <List
          dataSource={this.state.todos}
          renderItem={todo => (
            <List.Item>
              <Typography.Text mark>[ITEM]</Typography.Text> {todo.text}
            </List.Item>
          )}
        />
      </div>
    );
  }
}

export default App;
