import React, { Component } from "react";
import { Icon, Popover, Badge, List } from "antd";
import "antd/dist/antd.css";
import "./App.css";

class App extends Component {
  state = {
    notifications: []
  };

  componentDidMount = () => {
    let notifications = this.state.notifications;

    notifications.push({
      name: "Amr",
      msg: "commented on your post",
      isRead: false
    });
    notifications.push({
      name: "John",
      msg: "started following you",
      isRead: true
    });
    this.setState({ notifications });
  };

  render() {
    let iconClass = "icon-dimmed";

    if (this.state.notifications.length > 0) {
      iconClass = "icon-active";
    }

    const text = (
      <span>
        <b>Notifications</b>
      </span>
    );
    const content = (
      <div>
        {this.state.notifications.map(notification => (
          <p>
            <b>{notification.name}</b> {notification.msg}
          </p>
        ))}
      </div>
    );

    return (
      <div className="App">
        <div className="menuBar">
          <div className="logo">
            <a href="">Push Notification</a>
            count: {this.state.notifications.length}
          </div>
          <div className="menuCon">
            <div className="rightMenu">
              <a href="">
                <Popover
                  placement="bottomRight"
                  title={text}
                  content={content}
                  trigger="click"
                >
                  <Badge count={this.state.notifications.length} dot>
                    <Icon className={iconClass} type="bell" theme="filled" />
                  </Badge>
                </Popover>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
