import React, { Component } from "react";
import { Icon, Popover, Badge, List, Typography } from "antd";
import "antd/dist/antd.css";
import "./App.css";

class App extends Component {
  state = {
    newNotifications: [],
    notifications: []
  };

  componentDidMount = () => {
    let newNotifications = this.state.newNotifications;

    newNotifications.push({
      name: "Amr",
      msg: "commented on your post",
      isRead: false
    });
    newNotifications.push({
      name: "John",
      msg: "started following you",
      isRead: false
    });
    newNotifications.push({
      name: "Amr",
      msg: "commented on your post",
      isRead: true
    });
    this.setState({ newNotifications });
  };

  handleOnClick = () => {
    let notifications = this.state.notifications;
    notifications.push(this.state.newNotifications);

    this.setState({ newNotifications: [], notifications: notifications });
  };
  render() {
    let iconClass = "icon-dimmed";

    if (this.state.newNotifications.length > 0) {
      iconClass = "icon-active";
    }

    const text = (
      <span>
        <b>Notifications</b>
      </span>
    );
    const content = (
      <div>
        <List
          dataSource={this.state.notifications}
          renderItem={notification => (
            <List.Item
              className={notification.isRead ? "item-read" : "item-not-read"}
            >
              <span style={{ padding: "2px 20px" }}>
                <b>{notification.name}</b> {notification.msg}
              </span>
            </List.Item>
          )}
        />
      </div>
    );

    return (
      <div className="App">
        <div className="menuBar">
          <div className="logo">
            <a href="">Push Notification</a>
          </div>
          <div className="menuCon">
            <div className="rightMenu">
              <a href="" onClick={this.handleOnClick}>
                <Popover
                  placement="bottomRight"
                  title={text}
                  content={content}
                  trigger="click"
                >
                  <Badge
                    offset={[-2, 10]}
                    count={this.state.newNotifications.length}
                  >
                    <Icon className={iconClass} type="bell" theme="filled" />
                  </Badge>
                </Popover>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
