import React, { Component } from "react";
import { Menu, Row, Col, Input, Icon, Popover, Badge } from "antd";
import "antd/dist/antd.css";
import "./App.css";

const text = <span>Notifications</span>;
const content = (
  <div>
    <p>Content</p>
    <p>Content</p>
  </div>
);

class App extends Component {
  state = {
    notifications: []
  };

  render() {
    return (
      <div className="App">
        <div className="menuBar">
          <div className="logo">
            <a href="">Push Notification</a>
            count: {this.state.notifications.size()}
          </div>
          <div className="menuCon">
            <div className="rightMenu">
              <a href="">
                <Popover
                  placement="bottomRight"
                  title={text}
                  content={content}
                  trigger="click"
                >
                  <Badge count={this.state.notifications.size} dot>
                    <Icon type="bell" />
                  </Badge>
                </Popover>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
